        this.swalService.success('Usuario creado correctamente');
        this.router.navigate(['/admin/usuarios']);
        this.resetForm();
      },
      error: (error) => {
        console.error('Error al crear usuario:', error);
        this.swalService.error('Error al crear el usuario');
      }
    });
  }
}
resetForm(): void {
  this.user = {
    id: 0,
    name: '',
    lastname: '',
    email: '',
    role: [],
    username: '',
    password: '',
    specialties: []
